import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './card.css'

function card(props){

    // const [ActiveState, ChangeState] = useState(false);

    // const toggleState = () => {
    //     setActive(!ChangeState);
    // };

    return(
        
                <div className="col mb-5">
                    <div className="card h-100">
                        {props.sale ? (
                            <div className="badge bg-dark text-white position-absolute sale_style">Sale</div>
                        ):(
                            <div></div>
                        )}
                        <img className="card-img-top" src={props.image} alt="..." />
                        {props.discount ? (
                            <div>
                                <span className="text-muted text-decoration-line-through">
                                ${props.price1}
                                </span>
                                &nbsp;${props.price2}
                            </div>
                            ) : (
                            <div>
                                ${props.price1} - ${props.price2}
                            </div>
                        )}
                        <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                            <div className="text-center" onClick={()=>{props.AddToCart(props.name)}}><a className="btn btn-outline-dark mt-auto">{props.action}</a></div>
                        </div>
                    </div>
                </div>
            
    )
}

export default card;
